import React from "react";
import styled from "styled-components";
import Track from "./Track";
import data from '../data/fearless.json';
import { useState } from "react";


const Ul = styled.ul`{
  display: flex;
  gap: 10px;
  flex-direction: column;
}`

const Li = styled.li`{
  list-style: none;
  text-transform: capitalize;
  color: black;
  padding: 10px;
  font-size: 20px;
};`

const Button = styled.button`
  border: none;
  background-color: #f6ed95;
  border-radius: 5px;
  cursor: pointer;
;`


export default function Tracks() {

  const [tracks, setTracks] = useState(data);

  const handleToggle = (id) => {
    let mapped = data.tracks.map(track => {
      return track.id === id ? { ...track, complete: !track.complete } : { ...track};
    });
    setTracks(mapped);
  }
  return (
    <div>
      <h1>{data.title}</h1>
      <Ul>
        {data.tracks.map(track => {
          return (
            <Track tracks={track} handleToggle={handleToggle}/>
          )
        }
        )}
      </Ul>
    </div>
  );
}
