import React from "react";
import styled from "styled-components";
import data from "../data/fearless.json";


const Ul = styled.ul`{
  display: flex;
  gap: 10px;
  flex-direction: column;
}`

const Li = styled.li`{
  list-style: none;
  text-transform: capitalize;
  color: black;
  padding: 10px;
  font-size: 20px;
};`

const Button = styled.button`
  border: none;
  background-color: #f6ed95;
  border-radius: 5px;
  cursor: pointer;
