import './App.css';
import styled from "styled-components";
import Tracks from './components/Tracks';
// import data from './data/ts-discography.json';
import data from './services/getAlbums';
import { useEffect, useState } from "react";


const Container = styled.div`
  display: flex;
  flex-wrap: wrap;
  // align-items: center;
  // flex-direction: column;
`;

function App() {


  const [albums, setAlbums] = useState();

  const getAlbums = async () => {
    try {
        const response = await handleDepartmentsApi();
        setDepartments(response.data.departments)
    } catch (err) {
        //handle error or do whatever
    }
  }

  useEffect(() => {
    data()
  }, [])

  const handleToggle = (albumName) => {
    return (id) => {
      setAlbums(albums.map(album => {
        return album.title === albumName ? {
          ...album, tracks: album.tracks.map(track => {
            return track.id === Number(id) ? { ...track, special: !track.special } : { ...track };
          })
        } : { ...album };
      })
      )
    }
  }

  return (
    <Container>
      {albums.map(album => {

        return (
          <Tracks image={album.cover} color={album.color} tracks={album.tracks} handleToggle={handleToggle(album.title)} key={album.title} />
        )

      })}
    </Container>
  );
}

export default App;
