import './App.css';
import styled from "styled-components";
import Tracks from './components/Tracks';
import data from './data/ts-discography.json';
import { useState } from "react";


const Container = styled.div`
  display: flex;
  align-items: center;
  flex-direction: column;
`;

function App() {

  const [albums, setAlbums] = useState(data);
  const [tracks, setTracks] = useState(data.fearless);

  const handleToggle = (albumName) => {
    return (id) => {
      let mapped = tracks.map(track => {
        return track.id === Number(id) ? { ...track, complete: !track.complete } : { ...track };
      });

      setTracks(mapped);
    }
  }

  return (
    <Container>
      {Object.entries(albums).map(([albumName, tracks2]) => {
        console.log(albumName)
        return (
          <Tracks tracks={tracks2} handleToggle={handleToggle(albumName)} key={albumName} />
        )
      })}
    </Container>
  );
}

export default App;
