import './App.css';
import styled from "styled-components";
import Tracks from './components/Tracks';
import data from './data/ts-discography.json';
import { useState } from "react";


const Container = styled.div`
  display: flex;
  background-color: ${props => props.bgColor};
  // align-items: center;
  // flex-direction: column;
`;

function App() {

  const [albums, setAlbums] = useState(data.albums);

  const handleToggle = (albumName) => {
    return (id) => {
      setAlbums(albums.map(album => {
        return album.title === albumName ? {
          ...album, tracks: album.tracks.map(track => {
            return track.id === Number(id) ? { ...track, complete: !track.complete } : { ...track };
          })
        } : { ...album };
      })
      )
    }
  }

  return (
    <Container>
      {albums.map(album => {
        return (
          <Tracks bgtracks={album.tracks} handleToggle={handleToggle(album.title)} key={album.title} />
        )
      })}
    </Container>
  );
}

export default App;
