import './App.css';
import styled from "styled-components";
import Tracks from './components/Tracks';
import { handleAlbums, handleSpecial, putAlbum } from './services/handleAlbums';
import { useEffect, useState } from "react";
import { render } from '@testing-library/react';


const Container = styled.div`
  display: flex;
  flex-wrap: wrap;
`;

function App() {

  const [albums, setAlbums] = useState([]);
  const [special, setSpecial] = useState([])

  const getAlbums = async () => {
    try {
      const response = await handleAlbums();
      setAlbums(response)
    } catch (err) {
      console.log(err)
    }
  }

  const getSpecial = async () => {
    try {
      const response = await handleSpecial();
      setSpecial(response)
    } catch (err) {
      console.log(err)
    }
  }

  useEffect(() => {
    getAlbums()
    getSpecial()
  }, [])


  function markSpecial(album) {
    return {
      ...album, tracks: album.tracks.map(track => {
        special.slice(0, 2).map(latestTrack => {
          if (track.title === latestTrack.title) {
            return { ...track, latest: true }
          }
        })

        return special.includes(track.title) ? { ...track, special: true } : { ...track };
      })
    }
  }



  function markLatest() {
    special.slice(0, 2).map(track => {
      console.log(track)
      return (
        <div>
          <h1>{track.message}</h1>
          <p>latest</p>
        </div>
      )
    })
  }

  markLatest()


  // function toggleTrackSpecial(album, id) {
  //   return {
  //     ...album, tracks: album.tracks.map(track => {
  //       return track.id === Number(id) && !track.fixed ? { ...track, special: !track.special } : { ...track };
  //     })
  //   };
  // }

  const handleToggle = (albumName) => {
    return (id) => {
      const selectedAlbum = albums.find(album => album.title === albumName)
      const updatedAlbum = markSpecial(selectedAlbum, id)
      putAlbum(updatedAlbum)
      setAlbums(albums.map(album => {
        return album.title === albumName ? updatedAlbum : album;
      })
      )

    }
  }

  return (
    <Container>
      {albums.map(album => {

        return (
          <Tracks
            title={album.title}
            image={album.cover}
            color={album.color}
            tracks={album.tracks}
            handleToggle={handleToggle(album.title)} key={album.title} />
        )

      })}
    </Container>
  );

}

export default App;
