import React from "react";
import styled from "styled-components";
import data from "../data/fearless.json";


const Ul = styled.ul`{
  display: flex;
  gap: 10px;
  flex-direction: column;
}`

const Li = styled.li`{
  list-style: none;
  text-transform: capitalize;
  color: black;
  padding: 10px;
  font-size: 20px;
};`

const Button = styled.button`
  border: none;
  background-color: #f6ed95;
  border-radius: 5px;
  cursor: pointer;
;`

function Track({ track, index, markTrack }) {
  return (
    <div>
      <span style={{ textDecoration: track.isDone ? "line-through" : "" }}>{track.text}</span>
      <div>
        <Button variant="outline-success" onClick={() => markTrack(index)}>âœ“</Button>{' '}
      </div>
    </div>
  );
}

export default function Tracks() {

  const [tracks, setTracks] = React.useState({isDone: false});

  const markTrack = index => {
    const newTracks = [...tracks]
    newTracks[index].isDone = true;
    setTracks(newTracks);
};

  return (
    <div>
      <h1>{data.title}</h1>
      <Ul>
        {data.tracks.map((track, index) => (
          <Track>

            key={index}
            index={index}
            track={track}
            markTrack={markTrack}
          {track.number} - {track.title}</Track>
        ))}
      </Ul>
    </div>
  );
}
